package com.Practice;

public class Looping_dowhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=2;
		
		do {
			 
			i++;
			 //3 4 5 6
			     // 3
			System.out.println(i); //3 4 5 6
			                        //3
			
			
		}while(i>=5); // true 3>=5
		

	}

}
